import moment from "moment";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getAllEventByCalendar } from "../../../action/EventAction";

const DayPage = () => {
    const value_of_date = []
    const {day, calendar_id} = useParams()
    const event = useSelector(state => state.Event)
    const dispatch = useDispatch()

    useEffect( () => {
        dispatch(getAllEventByCalendar(calendar_id))
    },[dispatch])

    let AllEvent = event.events
    if (AllEvent === undefined) {
        AllEvent = []
    }
    console.log(calendar_id);
    const time = new Array(24)
    for (var i = 0; i < time.length; i++) {
        time[i] = new Array(4);
    }
    console.log(time);
    const bigtime = []
    for (let index = 0; index < 24; index+=1) {
        for (let i = 0; i < 60; i+=15) {
            if (index < 10 ) {
                if (i === 0) {
                    time[index][i] = `0${index}:0${i}`    
                }
                else {
                    time[index][i] =`0${index}:${i}`
                }
            } 

            else {
                if (i === 0) {
                    time[index][i] =`${index}:0${i}`
                }
                else {
                    time[index][i] = `${index}:${i}`
                }
            }
        }
    }
    for (let index = 0; index < 24; index++) {
        if(index < 10) {
            bigtime.push(`0${index}:00`)
        }
        else {
            bigtime.push(`${index}:00`)
        }

    }
    for (let index = 0; index < 24; index+=1) {
        for (let i = 0; i < 60; i+=15) {
            value_of_date.push(`${day} ${time[index][i]}`)
        }
    }
    for (let index = 0; index < 96; index++) {

    }
    let value_of_bigTime = []
    for (let index = 0; index < 24; index++){
        value_of_bigTime.push(`${day} ${bigtime}`)
    }
    console.log(bigtime);
    console.log(value_of_date);
    return(
        <>
            <table>
                <body>
                {bigtime.map( (date, index) => {
                    console.log(index);
                    return (
                        <tr value={value_of_bigTime[index]} key={value_of_bigTime[index]}>
                            <th>{date}</th>
                            {time.map(( tm, indx) => {
                                console.log(indx);
                                return (
                                    <tr>
                                        {time[index][indx]}
                                    </tr>
                                )
                            })}
                            {AllEvent.map((ev) => {
                                if (moment(ev.start_At).format('YYYY-MM-DD HH:mm').toString() === value_of_date[index]) {
                                    return(<td key={ev.id}>{ev.title}</td>)
                                }
                            })}
                        </tr>
                    )
                })}
                </body>
            </table>
        </>
    )
}

export default DayPage
