import { deleteComment } from "../api/CommentsApi";
import { createCommentsOnPost, createlike, deletelike, getCommentsOnPost, getLikesFromPost, getPost, PostByid } from "../api/PostApi"


export const getPosts = () => async(dispatch) => {
    try {
        const {data}= await getPost
        return dispatch({type:'getposts', payload:data})
    } catch (error) {
        console.log(error);
    }
}

export const GetLikes = (id) => async(dispatch) => {
    try {
        const {data} = await getLikesFromPost(id)
        return dispatch({type:'getlikes', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const getPostById = (id) => async(dispatch) => {
    try {
        const {data} = await PostByid(id)
        return dispatch({type:"getpostbyid", payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const getComments = (id) => async(dispatch) => {
    try {
        const {data} = await getCommentsOnPost(id)
        return dispatch({type:'getCommentsOnPost', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const createComment = (id, comment) => async(dispatch) => {
    try {
        const {data} = await createCommentsOnPost(id,comment)
        console.log(data);
        const Data = await getCommentsOnPost(id)
        let comments //vse kommentu
        if (data.result.affectedRows > 0) {
            comments = Data.data.result
            console.log(comments);
            let comment = comments[comments.length-1]
            console.log(comment);
            return dispatch({type:'createcommentforpost', payload:comment})
        }
    } catch (error) {
        console.log(error);
    }
}

export const likePost = (id) => async(dispatch) => {
    try {
        const {data} = await createlike(id)
        const Data = await getLikesFromPost(id)
        let likes
        if (data.result.affectedRows > 0) {
            likes = Data.data.result
            let like = likes[likes.length-1]
            dispatch({type:"likepost", payload:like})
        }
    } catch (error) {
        alert('You already paste like')
    }
}

export const DeletComment = (id,post_id) => async(dispatch) => {
    try {
        const {data} = await deleteComment(id)
        if (data.result.affectedRows > 0) {
            const Data = await getCommentsOnPost(post_id)
            return dispatch({type:'deletecomment',payload:Data.data.result})
        }   
    } catch (error) {
        alert("It`s not your acoount")
    }
}

export const deleteLikeFromPost = (id) => async(dispatch) => {
    try {
        const {data} = await deletelike(id)
        const Data = await getLikesFromPost(id)
        if (data.result.affectedRows>0) {
            let likes = Data.data.result
            let like = []
            for (let index = 0; index < likes.length; index++) {
                if (data.user === likes[index].user_id) {
                    console.log("Jopa");
                    index = index+1
                }
                like[index] = likes[index]
            }
            console.log(like);
            dispatch({type:'deletelike', payload:like})
        }
    } catch (error) {
        console.log(error);
    }
}
