import { Button } from "@mui/material";
import jwt_decode from "jwt-decode";
import moment from "moment";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { DeletComment, getComments, UpdateComment } from "../../../action/PostAction";
import DeleteIcon from '@mui/icons-material/Delete'
import EditIcon from '@mui/icons-material/Edit';
import { CSSTransition,TransitionGroup } from "react-transition-group";
import "../../style/PostPage.module.css"


import style from '../../style/PostPage.module.css'

const CommentsForPost = (props) => {
    const history = useHistory()
    let [comment, setComment] = useState('')
    let [index, setIndex] = useState(0)
    let {deleteIndex, setDeleteIndex} = useState(false)
    const auth = useSelector(state=>state.Auth)
    const token = auth.token
    const decode = jwt_decode(token)
    const user_id = decode.id
    const role = decode.role
    const {post_id,comments} = props

    const dispatch = useDispatch()
    useEffect ( () => {
        dispatch(getComments(post_id))
    }, [dispatch,post_id])
    if (comments.length === 0) {
        return (
            <p>No Comments yet</p>
        )
    }
    else {
        return (
            <>
            {role === 'admin' ? 
                <>
                    {comments.map( (cmnt) => 
                        <div key={cmnt.id} className={style.Comment}>
                            <a className={style.Commentlogin} onClick = {() => {history.push(`/user/${cmnt.user_id}`)}}>{cmnt.login}</a>
                            <p className={style.Commentcontent}>{cmnt.content}</p>
                            {user_id === cmnt.user_id ?
                            <>
                            <Button variant="outlined" endIcon={<EditIcon/>} className={style.EditButtonComment} onClick= { () => {
                                if (index === 0) {
                                    setIndex(1)
                                }
                                else if (index === 1) {
                                    setIndex(0)
                                    dispatch(UpdateComment(cmnt.id,post_id, comment))
                                }
                                console.log(index);
                            }}>Edit</Button>
                            {index === 1 ? <input type='text' placeholder="How u wanna edit comment" value={comment} onChange={e=>setComment(e.target.value)}/> :<></>}
                            </>
                            :
                            <></>
                        }
                            <Button variant="outlined" endIcon={<DeleteIcon />} sx= {{float:"right"}} className={style.DeleteButtonComment} onClick={() => {
                                dispatch(DeletComment(cmnt.id,post_id))
                            }}>Delete</Button>
                            <p className={style.Commenttime}>{moment(cmnt.time).fromNow()}</p>
                        </div>
                    )}
                </>
                :
                <>
                    <TransitionGroup>
                    {comments.map( (cmnt) => 
                    <CSSTransition
                        in = {deleteIndex}
                        key={cmnt.id}
                        timeout={500}
                        classNames={"item"}
                    >
                    <div key={cmnt.id} className={style.Comment}>
                        <a className={style.Commentlogin} onClick = {() => {history.push(`/user/${cmnt.user_id}`)}}>{cmnt.login}</a>
                        <p className={style.Commentcontent}>{cmnt.content}</p>
                        {user_id === cmnt.user_id ?
                            <>
                            <Button variant="outlined" endIcon={<EditIcon/>} className={style.EditButtonComment} onClick= { () => {
                                if (index === 0) {
                                    setIndex(1)
                                }
                                else if (index === 1) {
                                    setIndex(0)
                                    dispatch(UpdateComment(cmnt.id,post_id, comment))
                                }
                            }}>Edit</Button>
                            {index === 1 ? <input type='text' placeholder="How u wanna edit comment" value={comment} onChange={e=>setComment(e.target.value)}/> :<></>}
                            <Button variant="outlined" sx= {{float:"right"}} endIcon={<DeleteIcon />} className={style.DeleteButtonComment} onClick={() => {
                                setDeleteIndex(true)
                                dispatch(DeletComment(cmnt.id,post_id))
                            }}>Delete</Button>
                            </>
                            :
                            <></>
                        }
                        <p className={style.Commenttime}>{moment(cmnt.time).fromNow()}</p>
                    </div>
                    </CSSTransition>
                    )}
                    </TransitionGroup>
                </>
            }
            </>
        )
    }
}

export default  CommentsForPost
