import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import { useParams } from 'react-router-dom'
import PostSevice from '../API/PostService';
import CommentList from '../components/CommentList';
import Loader from '../components/UI/Loader/Loader';
import { useFetching } from '../hooks/useFetching';

const PostIdPage = () => {
    const params = useParams();
    const [post, setPost] = useState({})
    const [comments, setComments] =useState ([]);
    const [user, setUser] = useState([]);

    const [fetchPostById, isLoading, error] = useFetching(async () => {
        const response = await PostSevice.getById(params.id);
        setPost(response.data.post[0]);
    });
    const [fetchComments, isCommLoading, CommError] = useFetching(async () => {
        const response = await PostSevice.getPostComments(post.authorId);
        setComments(response.data.comments);
    });
    useEffect(() => {
        fetchPostById();
        fetchComments();
    }, []);
    const [fetchUser] = useFetching(async () => {
        const response = await UserSevice.getUserById(comment.authorId);
        const User = response.data.user[0];
        setUser(User);
    });
    useEffect(() => {
        fetchUser();
    }, []);
    return (
        <div>
            {isLoading
                ? <Loader />
                : <div>
                        <h2>{post.id}. {post.title}</h2>
                        <h2>{post.content}</h2>
                    </div>
            }
            {isCommLoading
                ? <Loader />
                : <CommentList comments={comments}/>
            }

        </div>
    )
}

export default PostIdPage
