import React from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import UserSevice from '../API/UserService';
import MyButton from '../components/UI/button/MyButton';
import MyInput from '../components/UI/input/MyInput';
import { useFetching } from '../hooks/useFetching';
import jwt from 'jwt-decode';


const Login = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();
    const isAuth = useSelector(state => state.status);

    const [fetchLogin, isUserLoading, userError] = useFetching(async () => {
        try{
            let response = await UserSevice.login(login, password);
            if(response.data.token){
                let token = response.data.token; 
                localStorage.setItem('token', token);
                let user = jwt(token);
                localStorage.setItem('user', user);
                dispatch({type: "LOGIN", payload: user});

            }
        }
        catch(err){
            console.log(err)
        }


    })
    function apiLogin(e){
        e.preventDefault();
        fetchLogin();

    }

    return (
        <div>
            <h1>Login </h1>
            <form>
                <MyInput
                    value={login}
                    type="text"
                    onChange={e => setLogin(e.target.value)}
                    placeholder="Login"
                />
                <MyInput
                    value={password}
                    type="password"
                    onChange={e => setPassword(e.target.value)}
                    placeholder="Password"
                />
                <MyButton onClick={(e) => apiLogin(e)}>Login</MyButton>
                <MyButton>Register</MyButton>
            </form>
        </div>
    )
}

export default Login
