import React, { useEffect, useState } from 'react';
import PostSevice from './API/PostService';
import PostFilter from './components/PostFilter';
import PostForm from './components/PostForm';
import PostList from './components/PostList';
import MyButton from './components/UI/button/MyButton';
import Loader from './components/UI/Loader/Loader';
import MyModal from './components/UI/MyModal/MyModal';
import { useFetching } from './hooks/useFetching';
import { usePosts } from './hooks/usePost';
import './styles/app.css'
import { getPagesArray, getPagesCount } from './utils/pages';
import { getPostsFromPage } from './utils/postCounter';

function App() {

  //const [posts, setPosts] = useState([]);
  const [posts, setPosts] = useState([
    { id: 1, title: 'Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 2, title: 'C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 3, title: 'Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 4, title: 'TS', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 5, title: 'C', description: 'About C++',publishDate: '2022 09 16' },
    { id: 6, title: 'Python', description: 'About Java',publishDate: '2022 09 16' },
    { id: 7, title: 'Ruby', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 8, title: 'Swift', description: 'About C++',publishDate: '2022 09 16' },
    { id: 9, title: 'Kotlin', description: 'About Java',publishDate: '2022 09 16' },
    { id: 10, title: 'PHP', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 12, title: '2C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 13, title: '2Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 13, title: '2Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 14, title: '2C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 15, title: '2Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 16, title: '2Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 17, title: '2C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 18, title: '2Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 19, title: '2Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 20, title: '2C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 21, title: '3Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 22, title: '3Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 23, title: '3C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 24, title: '3Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 25, title: '3Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 26, title: '3C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 27, title: '3Java', description: 'About Java',publishDate: '2022 09 16' },
    { id: 28, title: '3Javascript', description: 'About JS' ,publishDate: '2022 09 16' },
    { id: 29, title: '3C++', description: 'About C++',publishDate: '2022 09 16' },
    { id: 30, title: '3Java', description: 'About Java',publishDate: '2022 09 16' },
  ]);
  const [filter, setFilter] = useState({ sort: '', query: '' });
  const [modal, setModal] = useState(false);
  const [totalPages, setTotalPages] = useState(0);
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(3);
  const [postsToOutput, setpostsToOutput] = useState([]);

  let pagesArray = getPagesArray(totalPages);
  let arr = getPostsFromPage(limit, page, posts);
  // setpostsToOutput(getPostsFromPage(limit, page, posts));
  console.log(arr);

  const [fetchPosts, isPostLoading, postError] = useFetching(async () => {
    let response = await PostSevice.getAll();
    console.log(response.data.posts);
    response.data.posts.forEach(el => {
      let string = el.publishDate;
      let stringArr = string.split("T");
      stringArr = stringArr[0].split("-");
      el.publishDate = stringArr.join(" ");
    });
    //setPosts(response.data.posts);
    const totalCount = posts.length;
    setTotalPages(getPagesCount(totalCount, limit))
  })

  const createPost = (newPost) => {
    setPosts([newPost, ...posts]);
    setModal(false);
  }
  const removePost = (id) => {
    setPosts(posts.filter(p => p.id !== id))

  }
  const sortedAndSearchedPosts = usePosts(posts, filter.sort, filter.query);

  useEffect(() => {
    fetchPosts();
  }, [])

  return (
    <div className="App">
      {/* <MyButton onClick={fetchPosts}>Get Posts</MyButton> */}
      <MyButton onClick={() => setModal(true)}>
        Create post
      </MyButton>
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost} />
      </MyModal>
      <hr style={{ margin: '15px' }} />
      <PostFilter
        filter={filter}
        setFilter={setFilter}
      />
      {postError &&
        <h1>Ops, something wrong: {postError}</h1>
      }
      {isPostLoading
        ? <div className={"loaderDiv"}><Loader /></div>
        : <PostList remove={removePost} posts={sortedAndSearchedPosts} title={"Post list"} />
      }
      <div className="page__wrapper">
        {pagesArray.map(p =>
          <span
            onClick={() => setPage(p)}
            key={p}
            className={page === p ? "page page__current" : "page"}
          >
            {p}
          </span>
        )}
      </div>

    </div>

  );
}

export default App;
