import React from 'react'
import CommentItem from './CommItem'
import classes from './css/comment.module.css'
import MyButton from './UI/button/MyButton'

const CommentList = ({ comments, setModal, setCommentId, setbuttonText }) => {
    if (comments.length > 0) {
        return (
            <div>
                <h2>{comments.length} comments</h2>
                <MyButton onClick={()=> setModal(true)} >Add Comment</MyButton>
                <div className={classes.commenList}>
                    {comments.map((comment, index) =>
                        <div key={comment.id}>
                            <CommentItem comment={comment} index={index} comments={comments} setModal={setModal} setCommentId={setCommentId} setbuttonText={setbuttonText}/>
                            {/* <CommentItem comment={comment} index={index}  comments={comments}/> */}
                        </div>
                    )}

                </div>
            </div>
        )
    }
    else 
    {
        return (
            <div>
                <h2>No comments yet</h2>
                <MyButton 
                    onClick={()=> {
                        setModal(true)
                        setbuttonText("add")
                    }}>
                    Create Comment
                </MyButton>
                <div className={classes.commenList}></div>
            </div>

        )
    }

}

export default CommentList
