import React from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import UserSevice from '../API/UserService';
import MyButton from '../components/UI/button/MyButton';
import MyInput from '../components/UI/input/MyInput';
import { useFetching } from '../hooks/useFetching';


const Login = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();
    const isAuth = useSelector(state => state.status);

    const [fetchLogin, isUserLoading, userError] = useFetching(async () => {
        let response = await UserSevice.login(login, password);
        console.log(response);
    })
    function apiLogin(e){
        e.preventDefault();
        fetchLogin();
    }

    return (
        <div>
            <h1>Login </h1>
            <form>
                <MyInput
                    value={login}
                    type="text"
                    onChange={e => setLogin(e.target.value)}
                    placeholder="Login"
                />
                <MyInput
                    value={password}
                    type="password"
                    onChange={e => setPassword(e.target.value)}
                    placeholder="Password"
                />
                <MyButton onClick={(e) => apiLogin(e, login, password)}>Login</MyButton>
                <MyButton>Register</MyButton>
            </form>
        </div>
    )
}

export default Login
