import React, { useEffect } from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import UserSevice from '../API/UserService';
import MyButton from '../components/UI/button/MyButton';
import { useFetching } from '../hooks/useFetching';

const User = () => {
  const isAuth = useSelector(state => state);
  const [user, setUser] = useState({});

  const [fetchUser, isUserLoading, userError] = useFetching(async () => {
    let response = await UserSevice.getUserById(user.id);
    console.log(response.data);
    // setUser(response.data);
  })
  useEffect(() => {
    setUser(useSelector(state => state.user));
    fetchUser();
  }, []);
  const dispatch = useDispatch();
  function apiLogOut (){
    dispatch({type: "LOGOUT", payload: {}});
    localStorage.removeItem("token")
  }
  return (
    <div>
      User:
      {console.log(isAuth)}
      <div>
        <h5>login: {user.login}</h5>
        <h5>email: {user.email}</h5>
        <h5>role: {user.role}</h5>
      </div>
      <div><MyButton onClick={apiLogOut}>LogOut</MyButton></div>
    </div>
  )
}

export default User
