import React, { useState } from 'react';

function App() {
  let [likes, setLikes] = useState(5);
  let[value, setValue] = useState("text")

  function increment (){
    setLikes(likes += 1);
  }
  function decriment (){
    setLikes(likes -= 1);
  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <h1>{value}</h1>
      <input 
      type="text" 
      value={value}
      onChange={event => setValue(event.target.value)}
      />
     <button onClick={decriment}>-</button>
     <button onClick={increment}>+</button>
    </div>
  );
}

export default App;
