import React, { useState } from 'react';
import PostForm from './components/PostForm';
import PostList from './components/PostList';
import './styles/app.css'

function App() {

  const [posts, setPosts] = useState([
    { id: 1, title: 'Javascript', description: 'Description' },
    { id: 2, title: 'C++', description: 'Description' },
    { id: 3, title: 'Java', description: 'Description' }
  ]);

  const createPost = (newPost) =>{
    setPosts([...posts, newPost])   
  } 
  const removePost = (id) => {
    setPosts(posts.filter(p => p.id !== id))

  }

  return (
    <div className="App">
      <PostForm create={createPost}/>
      <hr style={{margin: '15px'}}/>
      <div>
        <select>
          <option value="value1">By name</option>
          <option value="value1">By descriprion</option>
        </select>
      </div>
      {posts.length !==0
        ?<PostList remove={removePost} posts={posts} title={"Post list"}/>
        :<h1>Posts not found!</h1>
      }
    </div>

  );
}

export default App;
