import React, { useEffect, useState } from 'react';
import PostSevice from './API/PostService';
import PostFilter from './components/PostFilter';
import PostForm from './components/PostForm';
import PostList from './components/PostList';
import MyButton from './components/UI/button/MyButton';
import Loader from './components/UI/Loader/Loader';
import MyModal from './components/UI/MyModal/MyModal';
import { useFetching } from './hooks/useFetching';
import { usePosts } from './hooks/usePost';
import './styles/app.css'
import { getPagesCount } from './utils/pages';

function App() {

  const [posts, setPosts] = useState([]);
  // const [posts, setPosts] = useState([
  //   { id: 1, title: 'Javascript', description: 'About JS' },
  //   { id: 2, title: 'C++', description: 'About C++' },
  //   { id: 3, title: 'Java', description: 'About Java' }
  // ]);
  const [filter, setFilter] = useState({ sort: '', query: '' });
  const [modal, setModal] = useState(false);
  const [totalPages, setTotalPages] = useState(0);
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(1);

  let pagesArray = [];
  for (let i = 0; i < totalPages; i++){
    pagesArray.push(i+1);
  }


  const [fetchPosts, isPostLoading, postError] = useFetching(async () => {
    let response = await PostSevice.getAll();
    console.log(response.data.posts);
    response.data.posts.forEach(el => {
      let string = el.publishDate;
      let stringArr = string.split("T");
      stringArr = stringArr[0].split("-");
      el.publishDate = stringArr.join(" ");
    });
    setPosts(response.data.posts);
    const totalCount = response.data.posts.length;
    setTotalPages(getPagesCount(totalCount, limit))
  })

  const createPost = (newPost) => {
    setPosts([newPost, ...posts]);
    setModal(false);
  }
  const removePost = (id) => {
    setPosts(posts.filter(p => p.id !== id))

  }
  const sortedAndSearchedPosts = usePosts(posts, filter.sort, filter.query);

  useEffect(() => {
    fetchPosts();
  }, [])

  return (
    <div className="App">
      {/* <MyButton onClick={fetchPosts}>Get Posts</MyButton> */}
      <MyButton onClick={() => setModal(true)}>
        Create post
      </MyButton>
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost} />
      </MyModal>
      <hr style={{ margin: '15px' }} />
      <PostFilter
        filter={filter}
        setFilter={setFilter}
      />
      {postError &&
        <h1>Ops, something wrong: {postError}</h1>
      }
      {isPostLoading
        ? <div className={"loaderDiv"}><Loader /></div>
        : <PostList remove={removePost} posts={sortedAndSearchedPosts} title={"Post list"} />
      }
    </div>

  );
}

export default App;
