import React, { useEffect, useState } from 'react';
import MyButton from './UI/button/MyButton';
import classes from './css/comment.module.css'
import { useFetching } from '../hooks/useFetching';
import UserSevice from '../API/UserService';
import { Link } from 'react-router-dom';

const CommentItem = ({comment, index, comments}) => {
    const [users, setUsers] = useState([]);
    const [fetchUsers] = useFetching(async () => {
        const response = await UserSevice.getAll();
        const UserArr = response.data.users;
        setUsers(UserArr);
    });
    useEffect(() => {
        fetchUsers();
    }, []);
    return (
        <div className={classes.comment}>
            <div className="comment__content">
                <div>
                    <p>{index + 1}</p>
                    {comment.commentId !==0 &&
                        <p>
                            replied to {comments.map((el, index) => {
                                if(el.id === comment.commentId){
                                    return comments.indexOf(el) + 1;
                                }
                            })}
                        </p>
                    }
                    <Link to={"/user/" + comment.authorId}>
                        {
                            users.map((el, index) => {
                                if(el.id===comment.authorId){
                                    return el.login
                                }
                            })
                        }
                    </Link>
                    <p>{comment.content}</p>
                </div>
                <div className="comment__btns">
                </div>
            </div>
        </div>
    )
}

export default CommentItem;
