import React, { useEffect, useState } from 'react';
import MyButton from './UI/button/MyButton';
import classes from './css/comment.module.css'
import { useFetching } from '../hooks/useFetching';
import UserSevice from '../API/UserService';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

const CommentItem = ({comment, index, comments, setModal, setCommentId, setbuttonText, likes}) => {
    const likeAction = (likes, commentId , status) =>{
        likes.forEach(like => {
            if(like.authorId === isAuthUser.id){
                return console.log(status + " on comment" +  commentId + " deleted")
            }
            else{
                return console.log(status + " on comment" +  commentId + " created")
            }
        })
    }
    const likeStatus = (likes) => {
        let status = "Like"
        likes.forEach(like => {
            if(like.authorId === isAuthUser.id && like.type === 1){
                console.log(comment.id, "Liked")
                status = "Liked"
            }
        })
        // console.log("like")
        return status
    }
    const dislikeStatus = (likes) => {
        let status = "Dislike"
        likes.forEach(like => {
            if(like.authorId === isAuthUser.id && like.type === -1){
                console.log(comment.id, "disliked")
                status = "Disliked"
            }
        })
        return status
    }
    // if(likes && likes.length > 0){
    //     console.log(likes)
    // }
    const [user, setUser] = useState([]);
    const isAuth = useSelector(state => state.status);
    const isAuthUser = useSelector(state => state.user);
    const [fetchUser] = useFetching(async () => {
        const response = await UserSevice.getUserById(comment.authorId);
        const User = response.data.user[0];
        setUser(User);
    });
    useEffect(() => {
        fetchUser();
    }, []);
    const sendReq = (e, commentId) =>{
        // e.preventDeafault();
        setCommentId(commentId);
        setModal(true);
        setbuttonText("Add");
    }
    return (
        <div className={classes.comment}>
            <div className="comment__content">
                <div>
                    <p>{index + 1}</p>
                    {comment.commentId !==0 &&
                        <p>
                            replied to {comments.map((el, index) => {
                                if(el.id === comment.commentId){
                                    return comments.indexOf(el) + 1;
                                }
                            })}
                        </p>
                    }
                    <Link to={"/user/" + comment.authorId}>{user.login}</Link>
                    <p>{comment.content}</p>
                </div>
                {isAuth &&
                    <div className="comment__btns">
                        <MyButton onClick={e => sendReq(e, comment.id)}>Reply</MyButton>
                        <MyButton onClick={() =>{likeAction(likes, comment.id, likeStatus(likes))}}>{likeStatus(likes)}</MyButton>
                        <MyButton onClick={() =>{likeAction(likes, comment.id, likeStatus(likes))}}>{dislikeStatus(likes)}</MyButton>
                    </div>
                }
            </div>
        </div>
    )
}

export default CommentItem;
