import User from './pages/User';
import './styles/app.css'
import { getPagesArray, getPagesCount } from './utils/pages';
import { getPostsFromPage } from './utils/postCounter';

function App() {
  return(
    <BrowserRouter>
      <Route path="/user">
        <User/>
